import { createSlice } from '@reduxjs/toolkit'
import axios from 'axios'

export const postsSlice = createSlice({
    name: 'posts',
    initialState: {
        value: {},
    },
    reducers: {
        initializePosts: (state) => {
            console.log('hiiii')
            const posts = axios.get('/getPosts').then((posts) => {
                console.log(posts.data)
            })
            console.log(posts)
            state.value = posts
        },
        getPosts: (state) => {
            return state.value
        }
    }

})

export const { getPosts, initializePosts } = postsSlice.actions

export default postsSlice.reducer
