import { useSelector } from 'react-redux'
import React from 'react'
import '../App.css';
const PostsList = () => {
    const posts = useSelector((state) => state.posts.value.posts)
    const [search, setSearch] = React.useState("")
    const [filteredPosts, setFilteredPosts] = React.useState([])

    console.log(posts)
    const setSelectedPost = () => {

    }


    const getSearchedItems = (value) => {
        const filteredPosts = posts.filter((post) => post.title.includes(value))
        setSearch(value)
        setFilteredPosts(filteredPosts)
    }
    return (
        <div className='post-list-component'>
            <input value={search} onChange={(event) => getSearchedItems(event.target.value)}></input>
            <Posts posts={filteredPosts} setSelectedPost={setSelectedPost} />
        </div >
    );
}



const Posts = (props) => {

    const posts = props.posts;
    return (posts.map((post) => <Post post={post} setSelectedPost={props.setSelectedPost} />))
}

const Post = (props) => {
    const { title, body } = props.post

    return (
        <div className='post' onClick={props.setSelectedPost}>
            <h2>{title}</h2>
            <div >{body}</div>
        </div>
    )
}

export default PostsList;
