import { createSlice } from '@reduxjs/toolkit'
import axios from 'axios'


export const postsSlice = createSlice({
    name: 'posts',
    initialState: {
        value: [],
    },
    reducers: {
        initializePosts: (posts, state) => {
            state.value = posts
        },
        selectPosts: (state) => {
            return state.value
        }
    }

})


export const fetchPosts = () => async dispatch => {
    const posts = await axios.get('/getPosts')
    console.log(posts)
    dispatch(initializePosts(posts))
}
export const { selectPosts, initializePosts } = postsSlice.actions

export default postsSlice.reducer
