import { createSlice } from '@reduxjs/toolkit'
import axios from 'axios'

export const postsSlice = createSlice({
    name: 'posts',
    initialState: {
        value: {},
    },
    reducers: {
        initializePosts: async (state) => {
            const posts = await axios.get('/getPosts')
            state.value = posts.data
        },
        getPosts: (state) => {
            return state.value
        }
    }

})

export const { getPosts, initializePosts } = postsSlice.actions

export default postsSlice.reducer
