import { createSlice } from '@reduxjs/toolkit'
import axios from 'axios'

export const postsSlice = createSlice({
    name: 'posts',
    initialState: {
        value: [],
    },
    reducers: {
        initializePosts: (state) => {
            const posts = getPosts()
            state.value = posts
        },
        selectPosts: (state) => {
            return state.value
        }
    }

})

const getPosts = () => {
    axios.get('/getPosts').then((posts) => posts.data)

}

export const { selectPosts, initializePosts } = postsSlice.actions

export default postsSlice.reducer
