import { createSlice } from '@reduxjs/toolkit'
import axios from 'axios'

export const postsSlice = createSlice({
    name: 'posts',
    initialState: {
        value: {},
    },
    reducers: {
        initializePosts: (state) => {
            axios.get('/getPosts').then((posts) => {
                state.value = posts
            })

        },
        getPosts: (state) => {
            return state.value
        }
    }

})

export const { getPosts, initializePosts } = postsSlice.actions

export default postsSlice.reducer
