import { useSelector } from 'react-redux'
import React from 'react'
import { post } from 'request'

const PostsList = () => {
    const posts = useSelector((state) => state.posts.value)
    const [search, setSearch] = React.useState('')
    const [filteredPosts, setFilteredPosts] = React.useState([])



    const getSearchedItems = (value) => {
        const filteredPosts = posts.find((value) => post.title.includes(value))

        setSearch(value)
        setFilteredPosts(filteredPosts)
    }
    return (
        <div >
            <input value={search} onchange={(event) => getSearchedItems(event.target.value)}></input>
            <Posts posts={filteredPosts.length > 0 ? filteredPosts : posts} />
        </div >
    );
}



const Posts = (props) => {
    const posts = props.posts;
    console.log(posts)
    return (posts.map((post) =>
        <div>
            {post.title}
        </div>

    ))
}

export default PostsList;
